mapper.select:
		select id,name,tel,r_d rD,l_d lD,r_cva rCva,l_cva lCva,tutor,gmt_modified gmtModified 
		from cus_consultation 
        
		limit #{startIndex},#{pageSize}

mapper.update:
update cus_consultation
name=#{name}, age=#{age}, gender=#{gender}, tel=#{tel}, eye=#{eye}, decline_time_year=#{declineTimeYear}, decline_time_month=#{declineTimeMonth}, diagnose=#{diagnose}, oculopathy=#{oculopathy}, correction_method=#{correctionMethod}, effect=#{effect}, visual_acuity=#{visualAcuity}, f_vision_condition=#{fVisionCondition}, f_other=#{fOther}, m_vision_condition=#{mVisionCondition}, m_other=#{mOther}, sleeping_time=#{sleepingTime}, eye_project=#{eyeProject}, eye_project_other=#{eyeProjectOther}, reading_time=#{readingTime}, reading_distance=#{readingDistance}, watching_time=#{watchingTime}, watching_distance=#{watchingDistance}, eye_position=#{eyePosition}, home_lighting_environment=#{homeLightingEnvironment}, classroom_lighting_environment=#{classroomLightingEnvironment}, r_d=#{rD}, l_d=#{lD}, r_cva=#{rCva}, l_cva=#{lCva}, tutor=#{tutor}, modified_user=#{modifiedUser}, gmt_modified=now()
where id=#{id}
